import React, { useEffect, useState } from 'react';
import { Container, Typography, CircularProgress, Alert, Box } from '@mui/material';
import { supabase } from '../supabaseClient';

const emptyItem = { name: '', description: '', price: '', image_url: '', category: '' };

function MenuManager() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState(emptyItem);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchItems = async () => {
    const { data, error } = await supabase.from('menu_items').select('*');
    if (error) setError(error.message);
    else setItems(data);
  };

  useEffect(() => { fetchItems(); }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!form.name || !form.price) {
      setError('Name and price are required.');
      return;
    }
    if (editingId) {
      const { error } = await supabase.from('menu_items').update(form).eq('id', editingId);
      if (error) setError(error.message);
      else {
        setSuccess('Item updated!');
        setEditingId(null);
        setForm(emptyItem);
        fetchItems();
      }
    } else {
      const { error } = await supabase.from('menu_items').insert([{ ...form, price: parseFloat(form.price) }]);
      if (error) setError(error.message);
      else {
        setSuccess('Item added!');
        setForm(emptyItem);
        fetchItems();
      }
    }
  };

  const handleEdit = item => {
    setEditingId(item.id);
    setForm({
      name: item.name,
      description: item.description,
      price: item.price,
      image_url: item.image_url,
      category: item.category,
    });
  };

  const handleDelete = async id => {
    if (!window.confirm('Delete this item?')) return;
    const { error } = await supabase.from('menu_items').delete().eq('id', id);
    if (error) setError(error.message);
    else {
      setSuccess('Item deleted!');
      fetchItems();
    }
  };

  const handleCancel = () => {
    setEditingId(null);
    setForm(emptyItem);
    setError('');
    setSuccess('');
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>Menu Management</Typography>
      {error && <Alert severity="error">{error}</Alert>}
      {success && <Alert severity="success">{success}</Alert>}
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
        <input name="description" placeholder="Description" value={form.description} onChange={handleChange} />
        <input name="price" placeholder="Price" type="number" step="0.01" value={form.price} onChange={handleChange} required />
        <input name="image_url" placeholder="Image URL" value={form.image_url} onChange={handleChange} />
        <input name="category" placeholder="Category" value={form.category} onChange={handleChange} />
        <button type="submit">{editingId ? 'Update' : 'Add'} Item</button>
        {editingId && <button type="button" onClick={handleCancel}>Cancel</button>}
      </form>
      <table border="1" cellPadding="8" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Name</th><th>Description</th><th>Price</th><th>Category</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item =>
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.description}</td>
              <td>${item.price}</td>
              <td>{item.category}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item.id)}>Delete</button>
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </Box>
  );
}

const AdminDashboard = () => {
  const [loading, setLoading] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const checkAdmin = async () => {
      setLoading(true);
      setError('');
      const { data: { user }, error: userError } = await supabase.auth.getUser();
      if (userError || !user) {
        setError('You must be logged in.');
        setLoading(false);
        return;
      }
      const { data, error: profileError } = await supabase
        .from('profiles')
        .select('role')
        .eq('id', user.id)
        .single();
      if (profileError || !data) {
        setError('Profile not found.');
        setLoading(false);
        return;
      }
      if (data.role === 'admin') {
        setIsAdmin(true);
      } else {
        setError('You are not authorized to view this page.');
      }
      setLoading(false);
    };
    checkAdmin();
  }, []);

  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 8, textAlign: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (error) {
    return (
      <Container maxWidth="sm" sx={{ mt: 8 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 8 }}>
      <Typography variant="h4" gutterBottom>
        Admin Dashboard
      </Typography>
      <Typography variant="h6">Welcome, Admin!</Typography>
      <MenuManager />
    </Container>
  );
};

export default AdminDashboard;
