import React, { useState, useEffect } from 'react';
import { Container, Grid, Box, Typography } from '@mui/material';
import Header from '../components/Header';
import Footer from '../components/Footer';
import CategoryTabs from '../components/CategoryTabs';
import MenuItemCard from '../components/MenuItemCard';
import { supabase } from '../supabaseClient';
import { useCart } from '../context/CartContext';

const Home = () => {
  const { addToCart } = useCart();
  const [menuItems, setMenuItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMenuItems = async () => {
      setLoading(true);
      const { data, error } = await supabase.from('menu_items').select('*');
      if (error) {
        console.error('Error fetching menu items:', error.message);
        setMenuItems([]);
        setCategories([]);
      } else {
        setMenuItems(data);
        // Extract unique categories from menu items
        const uniqueCategories = Array.from(new Set(data.map(item => item.category))).filter(Boolean);
        setCategories(uniqueCategories);
      }
      setLoading(false);
    };
    fetchMenuItems();
  }, []);

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: '#fafafa' }}>
      <Header />
      {/* Hero Banner */}
      <Box sx={{ width: '100%', height: 220, background: 'url(https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=1200&q=80) center/cover', display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 4 }}>
        <Typography variant="h3" color="white" sx={{ bgcolor: 'rgba(0,0,0,0.5)', px: 4, py: 2, borderRadius: 2, fontWeight: 'bold' }}>
          Order Your Favorite Food Online!
        </Typography>
      </Box>
      <Container maxWidth="lg">
        {loading ? (
          <Typography>Loading menu...</Typography>
        ) : (
          categories.map(category => (
            <Box key={category} sx={{ mb: 6 }}>
              <Typography variant="h5" sx={{ mb: 2, fontWeight: 'bold', color: 'primary.main' }}>{category}</Typography>
              <Grid container spacing={4}>
                {menuItems.filter(item => item.category === category).map(item => (
                  <Grid item key={item.id} xs={12} sm={6} md={4} lg={3}>
                    <MenuItemCard item={{...item, image: item.image_url}} onAddToCart={addToCart} />
                  </Grid>
                ))}
              </Grid>
            </Box>
          ))
        )}
      </Container>
      <Footer />
    </Box>
  );
};

export default Home;
