import React, { useEffect, useState } from 'react';
import {
  Box, Typography, Alert, Button, TextField, Paper, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent,
  DialogContentText, DialogActions, Stack, Select, MenuItem, InputLabel, FormControl
} from '@mui/material';
import { supabase } from '../supabaseClient';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const emptyCategory = { name: '', type: 'category' };
const emptySubcategory = { name: '', parent_id: null, type: 'subcategory' };

function CategoryManager() {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [form, setForm] = useState(emptyCategory);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [deleteId, setDeleteId] = useState(null);
  const [isSubcategory, setIsSubcategory] = useState(false);

  const fetchCategories = async () => {
    const { data, error } = await supabase.from('categories').select('*').eq('type', 'category');
    if (error) setError(error.message);
    else setCategories(data);
  };

  const fetchSubcategories = async () => {
    const { data, error } = await supabase.from('categories').select('*').eq('type', 'subcategory');
    if (error) setError(error.message);
    else setSubcategories(data);
  };

  useEffect(() => {
    fetchCategories();
    fetchSubcategories();
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!form.name) {
      setError('Name is required.');
      return;
    }
    if (isSubcategory && !form.parent_id) {
      setError('Please select a parent category.');
      return;
    }
    const submitData = {
      ...form,
    };
    if (editingId) {
      const { error } = await supabase.from('categories').update(submitData).eq('id', editingId);
      if (error) setError(error.message);
      else {
        setSuccess('Item updated!');
        setEditingId(null);
        setForm(isSubcategory ? emptySubcategory : emptyCategory);
        fetchCategories();
        fetchSubcategories();
      }
    } else {
      const { error } = await supabase.from('categories').insert([submitData]);
      if (error) setError(error.message);
      else {
        setSuccess('Item added!');
        setForm(isSubcategory ? emptySubcategory : emptyCategory);
        fetchCategories();
        fetchSubcategories();
      }
    }
  };

  const handleEdit = item => {
    setEditingId(item.id);
    setForm({
      name: item.name,
      type: item.type,
      parent_id: item.parent_id,
    });
    setIsSubcategory(item.type === 'subcategory');
  };

  const handleDelete = async () => {
    if (!deleteId) return;
    const { error } = await supabase.from('categories').delete().eq('id', deleteId);
    if (error) setError(error.message);
    else {
      setSuccess('Item deleted!');
      fetchCategories();
      fetchSubcategories();
    }
    setDeleteId(null);
  };

  const handleCancel = () => {
    setEditingId(null);
    setForm(isSubcategory ? emptySubcategory : emptyCategory);
    setError('');
    setSuccess('');
  };

  const toggleType = () => {
    setIsSubcategory(!isSubcategory);
    setForm(isSubcategory ? emptyCategory : emptySubcategory);
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>Category Management</Typography>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
      <Paper sx={{ p: 2, mb: 4 }}>
        <form onSubmit={handleSubmit}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} alignItems="center">
            <TextField
              name="name"
              label={isSubcategory ? 'Subcategory Name' : 'Category Name'}
              value={form.name}
              onChange={handleChange}
              required
              size="small"
            />
            {isSubcategory && (
              <FormControl size="small" sx={{ minWidth: 180 }} required>
                <InputLabel id="parent-category-label">Parent Category</InputLabel>
                <Select
                  labelId="parent-category-label"
                  name="parent_id"
                  value={form.parent_id || ''}
                  label="Parent Category"
                  onChange={e => setForm({ ...form, parent_id: e.target.value })}
                >
                  {categories.map(cat => (
                    <MenuItem key={cat.id} value={cat.id}>{cat.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            )}
            <Button type="button" variant="outlined" onClick={toggleType}>
              {isSubcategory ? 'Add Category' : 'Add Subcategory'}
            </Button>
            <Button type="submit" variant="contained" color="primary">
              {editingId ? 'Update' : 'Add'}
            </Button>
            {editingId && (
              <Button type="button" variant="outlined" color="secondary" onClick={handleCancel}>
                Cancel
              </Button>
            )}
          </Stack>
        </form>
      </Paper>
      <Typography variant="h6" gutterBottom>Categories</Typography>
      <TableContainer component={Paper} sx={{ mb: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Actions</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {categories.map(item => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEdit(item)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => setDeleteId(item.id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Typography variant="h6" gutterBottom>Subcategories</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Parent ID</b></TableCell>
              <TableCell><b>Actions</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {subcategories.map(item => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.parent_id}</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEdit(item)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => setDeleteId(item.id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Dialog open={!!deleteId} onClose={() => setDeleteId(null)}>
        <DialogTitle>Delete Item</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteId(null)}>Cancel</Button>
          <Button onClick={handleDelete} color="error">Delete</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default CategoryManager;
