import React, { useState, useEffect, useCallback } from 'react';
import { Container, Typography, Box, Button, Grid, Card, CardContent, IconButton, Alert, Snackbar, TextField, InputAdornment, CircularProgress, Divider, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { supabase } from '../supabaseClient';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

// Initialize Stripe
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

// Payment Form Component
const PaymentForm = ({ amount, onSuccess, onError }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProcessing(true);
    setError(null);

    if (!stripe || !elements) {
      return;
    }

    try {
      const { error: stripeError, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: elements.getElement(CardElement),
      });

      if (stripeError) {
        setError(stripeError.message);
        onError(stripeError.message);
        return;
      }

      onSuccess(paymentMethod);
    } catch (err) {
      setError(err.message);
      onError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <Box sx={{ mb: 2 }}>
        <CardElement
          options={{
            style: {
              base: {
                fontSize: '16px',
                color: '#424770',
                '::placeholder': {
                  color: '#aab7c4',
                },
              },
              invalid: {
                color: '#9e2146',
              },
            },
          }}
        />
      </Box>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      <Button
        type="submit"
        variant="contained"
        color="primary"
        fullWidth
        disabled={!stripe || processing}
      >
        {processing ? 'Processing...' : `Pay ${amount.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}`}
      </Button>
    </form>
  );
};

const Cart = () => {
  const { cart, removeFromCart, updateQuantity } = useCart();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponError, setCouponError] = useState('');
  const [isCheckingCoupon, setIsCheckingCoupon] = useState(false);
  const [couponStatus, setCouponStatus] = useState(null);
  const [validatedCoupon, setValidatedCoupon] = useState(null);
  const [showPaymentDialog, setShowPaymentDialog] = useState(false);
  const [clientSecret, setClientSecret] = useState(null);

  // Calculate subtotal
  const calculateSubtotal = () => {
    return Number(cart.reduce((sum, item) => {
      const itemTotal = Number(item.price) * Number(item.quantity);
      return sum + Number(itemTotal.toFixed(2));
    }, 0).toFixed(2));
  };

  // Calculate discount amount
  const calculateDiscount = (coupon) => {
    if (!coupon) return 0;
    const subtotal = calculateSubtotal();
    let discount = 0;
    
    if (coupon.discount_type === 'percentage') {
      discount = Number((subtotal * Number(coupon.discount_value) / 100).toFixed(2));
      if (coupon.max_discount) {
        discount = Math.min(discount, Number(coupon.max_discount));
      }
    } else {
      discount = Math.min(Number(coupon.discount_value), subtotal);
    }
    
    return Number(discount.toFixed(2));
  };

  // Calculate total
  const calculateTotal = () => {
    const subtotal = calculateSubtotal();
    const coupon = appliedCoupon || validatedCoupon;
    const discount = calculateDiscount(coupon);
    return Number((subtotal - discount).toFixed(2));
  };

  // Format amount for Stripe (convert to pence)
  const formatAmountForStripe = (amount) => {
    // Convert to pence and ensure it's an integer
    const amountInPence = Math.round(Number(amount) * 100);
    if (!Number.isInteger(amountInPence) || amountInPence < 0) {
      throw new Error(`Invalid amount: ${amount}`);
    }
    return amountInPence;
  };

  // Debounced coupon check
  const debouncedCheckCoupon = useCallback(
    async (code) => {
      if (!code) {
        setCouponStatus(null);
        setCouponError('');
        setValidatedCoupon(null);
        return;
      }

      setIsCheckingCoupon(true);
      try {
        const { data, error } = await supabase
          .from('coupons')
          .select('*')
          .ilike('code', code.toUpperCase())
          .eq('is_active', true)
          .single();

        if (error) {
          if (error.code === 'PGRST116') {
            setCouponStatus('invalid');
            setCouponError('Invalid coupon code');
            setValidatedCoupon(null);
          } else {
            throw error;
          }
          return;
        }

        if (!data) {
          setCouponStatus('invalid');
          setCouponError('Invalid coupon code');
          setValidatedCoupon(null);
          return;
        }

        // Check if coupon is valid
        const now = new Date();
        const startDate = new Date(data.start_date);
        const endDate = new Date(data.end_date);

        if (now < startDate || now > endDate) {
          setCouponStatus('invalid');
          setCouponError('Coupon is not valid at this time');
          setValidatedCoupon(null);
          return;
        }

        // Check minimum order amount
        const subtotal = calculateSubtotal();
        if (data.min_order_amount && subtotal < data.min_order_amount) {
          setCouponStatus('invalid');
          setCouponError(`Minimum order amount is Â£${data.min_order_amount}`);
          setValidatedCoupon(null);
          return;
        }

        setCouponStatus('valid');
        setCouponError('');
        setValidatedCoupon(data);
      } catch (err) {
        console.error('Coupon check error:', err);
        setCouponStatus('invalid');
        setCouponError(err.message);
        setValidatedCoupon(null);
      } finally {
        setIsCheckingCoupon(false);
      }
    },
    [cart]
  );

  // Set up debounced effect
  useEffect(() => {
    const timer = setTimeout(() => {
      debouncedCheckCoupon(couponCode);
    }, 500);

    return () => clearTimeout(timer);
  }, [couponCode, debouncedCheckCoupon]);

  const handleApplyCoupon = async () => {
    if (couponStatus !== 'valid') return;
    setAppliedCoupon(validatedCoupon);
  };

  const handleRemoveCoupon = () => {
    setAppliedCoupon(null);
    setValidatedCoupon(null);
    setCouponCode('');
    setCouponStatus(null);
    setCouponError('');
  };

  const handleCheckout = async () => {
    try {
      setLoading(true);
      setError('');
      
      const { data: { user } } = await supabase.auth.getUser();
      
      const subtotal = calculateSubtotal();
      const coupon = appliedCoupon || validatedCoupon;
      const discount = calculateDiscount(coupon);
      const finalTotal = calculateTotal();
      
      if (subtotal <= 0) {
        throw new Error('Invalid order amount');
      }
      
      if (discount < 0 || discount > subtotal) {
        throw new Error('Invalid discount amount');
      }
      
      if (finalTotal <= 0) {
        throw new Error('Invalid total amount');
      }

      // Format items for server with proper price handling
      const formattedItems = cart.map(item => {
        const price = parseFloat(item.price);
        const quantity = parseInt(item.quantity);
        
        if (isNaN(price) || price < 0) {
          throw new Error(`Invalid price for item ${item.name}`);
        }
        if (isNaN(quantity) || quantity < 1) {
          throw new Error(`Invalid quantity for item ${item.name}`);
        }

        return {
          ...item,
          price: price.toFixed(2),
          quantity: quantity
        };
      });
      
      // Format amounts for Stripe
      const stripeSubtotal = formatAmountForStripe(subtotal);
      const stripeDiscount = formatAmountForStripe(discount);
      const stripeTotal = formatAmountForStripe(finalTotal);

      // Create payment intent
      const response = await fetch('http://localhost:3001/api/create-payment-intent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          items: formattedItems,
          userId: user?.id || null,
          couponId: coupon?.id || null,
          subtotal: stripeSubtotal,
          discount: stripeDiscount,
          total: stripeTotal
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || data.message || 'Failed to create payment intent');
      }

      setClientSecret(data.clientSecret);
      setShowPaymentDialog(true);
    } catch (error) {
      console.error('Checkout error:', error);
      setError(error.message || 'Failed to process checkout. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePaymentSuccess = async (paymentMethod) => {
    try {
      setLoading(true);
      
      // Confirm the payment
      const response = await fetch('http://localhost:3001/api/confirm-payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          paymentMethodId: paymentMethod.id,
          clientSecret,
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || 'Payment failed');
      }

      // Clear cart and show success message
      // You'll need to implement this in your CartContext
      // clearCart();
      setShowPaymentDialog(false);
      navigate('/success');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  if (cart.length === 0) {
    return (
      <Container>
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography variant="h5">Your cart is empty</Typography>
          <Button variant="contained" color="primary" onClick={() => navigate('/')} sx={{ mt: 2 }}>
            Continue Shopping
          </Button>
        </Box>
      </Container>
    );
  }

  const subtotal = calculateSubtotal();
  const discount = calculateDiscount(appliedCoupon || validatedCoupon);
  const total = calculateTotal();

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 4 }}>Your Cart</Typography>
      
      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={() => setError('')} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          {cart.map((item) => (
            <Card key={item.id} sx={{ mb: 2 }}>
              <CardContent>
                <Grid container alignItems="center" spacing={2}>
                  <Grid item xs={12} sm={3}>
                    {item.image_url && (
                      <img 
                        src={item.image_url} 
                        alt={item.name} 
                        style={{ width: '100%', height: 'auto', borderRadius: '4px' }} 
                      />
                    )}
                  </Grid>
                  <Grid item xs={12} sm={5}>
                    <Typography variant="h6">{item.name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {item.price !== undefined && item.price !== null ?
                        item.price.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' }) :
                        ''}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} sm={2}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <IconButton 
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        disabled={item.quantity <= 1}
                      >
                        <RemoveIcon />
                      </IconButton>
                      <Typography sx={{ mx: 1 }}>{item.quantity}</Typography>
                      <IconButton onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                        <AddIcon />
                      </IconButton>
                    </Box>
                  </Grid>
                  <Grid item xs={12} sm={2} sx={{ textAlign: 'right' }}>
                    <IconButton color="error" onClick={() => removeFromCart(item.id)}>
                      <DeleteIcon />
                    </IconButton>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          ))}
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Order Summary
              </Typography>
              
              {/* Coupon Input */}
              <Box sx={{ mb: 2 }}>
                <TextField
                  fullWidth
                  label="Coupon Code"
                  value={couponCode}
                  onChange={(e) => setCouponCode(e.target.value)}
                  error={couponStatus === 'invalid'}
                  helperText={couponError}
                  disabled={!!appliedCoupon}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        {isCheckingCoupon ? (
                          <CircularProgress size={20} />
                        ) : couponStatus === 'valid' ? (
                          <IconButton
                            onClick={handleApplyCoupon}
                            color="success"
                          >
                            <CheckCircleIcon />
                          </IconButton>
                        ) : couponStatus === 'invalid' ? (
                          <IconButton color="error">
                            <ErrorIcon />
                          </IconButton>
                        ) : (
                          <IconButton
                            onClick={handleApplyCoupon}
                            disabled={!couponCode}
                          >
                            <LocalOfferIcon />
                          </IconButton>
                        )}
                      </InputAdornment>
                    ),
                  }}
                />
                {(appliedCoupon || validatedCoupon) && (
                  <Box sx={{ mt: 1, p: 1, bgcolor: 'success.light', borderRadius: 1 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Typography variant="body2" color="success.contrastText">
                        Coupon {appliedCoupon ? 'applied' : 'available'}: {(appliedCoupon || validatedCoupon).code}
                      </Typography>
                      {appliedCoupon && (
                        <IconButton 
                          size="small" 
                          onClick={handleRemoveCoupon}
                          sx={{ color: 'success.contrastText' }}
                        >
                          <DeleteIcon fontSize="small" />
                        </IconButton>
                      )}
                    </Box>
                    <Typography variant="body2" color="success.contrastText">
                      {(appliedCoupon || validatedCoupon).discount_type === 'percentage' 
                        ? `${(appliedCoupon || validatedCoupon).discount_value}% off`
                        : `Â£${(appliedCoupon || validatedCoupon).discount_value} off`}
                    </Typography>
                  </Box>
                )}
              </Box>

              <Divider sx={{ my: 2 }} />

              <Box sx={{ mt: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body1">Subtotal:</Typography>
                  <Typography variant="body1">
                    {subtotal.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                  </Typography>
                </Box>
                
                {(appliedCoupon || validatedCoupon) && (
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="body1" color="success.main">Discount:</Typography>
                    <Typography variant="body1" color="success.main">
                      -{discount.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                    </Typography>
                  </Box>
                )}

                <Divider sx={{ my: 1 }} />
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                  <Typography variant="h6">Total:</Typography>
                  <Typography variant="h6">
                    {total.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                  </Typography>
                </Box>

                <Button 
                  variant="contained" 
                  color="primary" 
                  fullWidth 
                  onClick={handleCheckout}
                  disabled={loading}
                  sx={{ mt: 2 }}
                >
                  {loading ? 'Processing...' : 'Checkout'}
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Payment Dialog */}
      <Dialog 
        open={showPaymentDialog} 
        onClose={() => setShowPaymentDialog(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Complete Your Payment</DialogTitle>
        <DialogContent>
          {clientSecret && (
            <Elements stripe={stripePromise} options={{ clientSecret }}>
              <PaymentForm
                amount={total}
                onSuccess={handlePaymentSuccess}
                onError={(error) => setError(error)}
              />
            </Elements>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowPaymentDialog(false)}>Cancel</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Cart;
