import React, { useState, useEffect, useCallback } from 'react';
import { Container, Typography, Box, Button, Grid, Card, CardContent, IconButton, Alert, Snackbar, TextField, InputAdornment, CircularProgress } from '@mui/material';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import { supabase } from '../supabaseClient';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity } = useCart();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponError, setCouponError] = useState('');
  const [isCheckingCoupon, setIsCheckingCoupon] = useState(false);
  const [couponStatus, setCouponStatus] = useState(null); // 'valid', 'invalid', or null

  // Debounced coupon check
  const debouncedCheckCoupon = useCallback(
    async (code) => {
      if (!code) {
        setCouponStatus(null);
        setCouponError('');
        return;
      }

      setIsCheckingCoupon(true);
      try {
        const { data, error } = await supabase
          .from('coupons')
          .select('*')
          .ilike('code', code.toUpperCase())
          .eq('is_active', true)
          .single();

        if (error) {
          if (error.code === 'PGRST116') {
            setCouponStatus('invalid');
            setCouponError('Invalid coupon code');
          } else {
            throw error;
          }
          return;
        }

        if (!data) {
          setCouponStatus('invalid');
          setCouponError('Invalid coupon code');
          return;
        }

        // Check if coupon is valid
        const now = new Date();
        const startDate = new Date(data.start_date);
        const endDate = new Date(data.end_date);

        if (now < startDate || now > endDate) {
          setCouponStatus('invalid');
          setCouponError('Coupon is not valid at this time');
          return;
        }

        // Check minimum order amount
        const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
        if (data.min_order_amount && subtotal < data.min_order_amount) {
          setCouponStatus('invalid');
          setCouponError(`Minimum order amount is Â£${data.min_order_amount}`);
          return;
        }

        setCouponStatus('valid');
        setCouponError('');
      } catch (err) {
        console.error('Coupon check error:', err);
        setCouponStatus('invalid');
        setCouponError(err.message);
      } finally {
        setIsCheckingCoupon(false);
      }
    },
    [cart]
  );

  // Set up debounced effect
  useEffect(() => {
    const timer = setTimeout(() => {
      debouncedCheckCoupon(couponCode);
    }, 500); // 500ms delay

    return () => clearTimeout(timer);
  }, [couponCode, debouncedCheckCoupon]);

  const handleApplyCoupon = async () => {
    if (couponStatus !== 'valid') return;

    try {
      const { data, error } = await supabase
        .from('coupons')
        .select('*')
        .ilike('code', couponCode.toUpperCase())
        .eq('is_active', true)
        .single();

      if (error) throw error;
      setAppliedCoupon(data);
      setCouponError('');
    } catch (err) {
      console.error('Coupon application error:', err);
      setCouponError(err.message);
    }
  };

  const calculateTotal = () => {
    const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    if (!appliedCoupon) return subtotal;

    let discount = 0;
    if (appliedCoupon.discount_type === 'percentage') {
      discount = (subtotal * appliedCoupon.discount_value) / 100;
      if (appliedCoupon.max_discount) {
        discount = Math.min(discount, appliedCoupon.max_discount);
      }
    } else {
      discount = appliedCoupon.discount_value;
    }

    return subtotal - discount;
  };

  const handleCheckout = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Get current user
      const { data: { user } } = await supabase.auth.getUser();
      
      console.log('Sending cart items:', cart);
      
      const response = await fetch('http://localhost:3001/api/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          items: cart,
          userId: user?.id || null,
          couponId: appliedCoupon?.id || null
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.error || data.message || 'Failed to create checkout session');
      }

      if (!data.url) {
        throw new Error('No checkout URL received from server');
      }

      console.log('Redirecting to checkout URL:', data.url);
      window.location.href = data.url;
    } catch (error) {
      console.error('Checkout error:', error);
      setError(error.message || 'Failed to process checkout. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const total = calculateTotal();

  if (cart.length === 0) {
    return (
      <Container>
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography variant="h5">Your cart is empty</Typography>
          <Button variant="contained" color="primary" onClick={() => navigate('/')} sx={{ mt: 2 }}>
            Continue Shopping
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 4 }}>Your Cart</Typography>
      
      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError('')}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert onClose={() => setError('')} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          {cart.map((item) => (
            <Card key={item.id} sx={{ mb: 2 }}>
              <CardContent>
                <Grid container alignItems="center" spacing={2}>
                  <Grid item xs={12} sm={3}>
                    <img src={item.image} alt={item.name} style={{ width: '100%', height: 'auto' }} />
                  </Grid>
                  <Grid item xs={12} sm={5}>
                    <Typography variant="h6">{item.name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {item.price !== undefined && item.price !== null ?
                        item.price.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' }) :
                        ''}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} sm={2}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <IconButton onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                        <RemoveIcon />
                      </IconButton>
                      <Typography sx={{ mx: 1 }}>{item.quantity}</Typography>
                      <IconButton onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                        <AddIcon />
                      </IconButton>
                    </Box>
                  </Grid>
                  <Grid item xs={12} sm={2}>
                    <IconButton color="error" onClick={() => removeFromCart(item.id)}>
                      <DeleteIcon />
                    </IconButton>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          ))}
        </Grid>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Order Summary
              </Typography>
              
              {/* Coupon Input */}
              <Box sx={{ mb: 2 }}>
                <TextField
                  fullWidth
                  label="Coupon Code"
                  value={couponCode}
                  onChange={(e) => setCouponCode(e.target.value)}
                  error={couponStatus === 'invalid'}
                  helperText={couponError}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        {isCheckingCoupon ? (
                          <CircularProgress size={20} />
                        ) : couponStatus === 'valid' ? (
                          <IconButton
                            onClick={handleApplyCoupon}
                            color="success"
                          >
                            <CheckCircleIcon />
                          </IconButton>
                        ) : couponStatus === 'invalid' ? (
                          <IconButton color="error">
                            <ErrorIcon />
                          </IconButton>
                        ) : (
                          <IconButton
                            onClick={handleApplyCoupon}
                            disabled={!couponCode}
                          >
                            <LocalOfferIcon />
                          </IconButton>
                        )}
                      </InputAdornment>
                    ),
                  }}
                />
                {appliedCoupon && (
                  <Box sx={{ mt: 1, p: 1, bgcolor: 'success.light', borderRadius: 1 }}>
                    <Typography variant="body2" color="success.contrastText">
                      Coupon applied: {appliedCoupon.code}
                    </Typography>
                    <Typography variant="body2" color="success.contrastText">
                      {appliedCoupon.discount_type === 'percentage' 
                        ? `${appliedCoupon.discount_value}% off`
                        : `Â£${appliedCoupon.discount_value} off`}
                    </Typography>
                  </Box>
                )}
              </Box>

              <Box sx={{ mt: 2 }}>
                <Typography variant="body1" sx={{ mb: 1 }}>
                  Subtotal: {cart.reduce((sum, item) => sum + item.price * item.quantity, 0).toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                </Typography>
                {appliedCoupon && (
                  <Typography variant="body1" color="success.main" sx={{ mb: 1 }}>
                    Discount: -{(cart.reduce((sum, item) => sum + item.price * item.quantity, 0) - total).toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                  </Typography>
                )}
                <Typography variant="h6" sx={{ mb: 2 }}>
                  Total: {total.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                </Typography>
              </Box>
              <Button 
                variant="contained" 
                color="primary" 
                fullWidth 
                onClick={handleCheckout}
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Checkout'}
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cart;
