import React from 'react';
import { Container, Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Button, Box, TextField } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, clearCart } = useCart();

  const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Shopping Cart
      </Typography>
      {cart.length === 0 ? (
        <Typography>Your cart is empty.</Typography>
      ) : (
        <>
          <List>
            {cart.map((item) => (
              <ListItem key={item.id} divider>
                <ListItemText
                  primary={item.name}
                  secondary={
                    <>
                      Quantity:&nbsp;
                      <TextField
                        type="number"
                        size="small"
                        value={item.quantity}
                        inputProps={{ min: 1, style: { width: 50 } }}
                        onChange={e => {
                          const qty = Math.max(1, parseInt(e.target.value) || 1);
                          updateQuantity(item.id, qty);
                        }}
                        sx={{ width: 70 }}
                      />
                    </>
                  }
                />
                <ListItemText
                  primary={`$${(item.price * item.quantity).toFixed(2)}`}
                  align="right"
                />
                <ListItemSecondaryAction>
                  <IconButton edge="end" aria-label="delete" onClick={() => removeFromCart(item.id)}>
                    <DeleteIcon />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
          <Box sx={{ mt: 4, textAlign: 'right' }}>
            <Typography variant="h5" gutterBottom>
              Total: ${total.toFixed(2)}
            </Typography>
            <Button variant="contained" color="primary" size="large" sx={{ mr: 2 }}>
              Proceed to Checkout
            </Button>
            <Button variant="outlined" color="secondary" size="large" onClick={clearCart}>
              Clear Cart
            </Button>
          </Box>
        </>
      )}
    </Container>
  );
};

export default Cart;
