import React, { useState, useEffect } from 'react';
import {
  Container,
  Tabs,
  Tab,
  Box,
  Typography,
  Button,
  Paper,
} from '@mui/material';
import { useMenu } from '../context/MenuContext';
import { useCart } from '../context/CartContext';

const Menu = () => {
  const { menuItems, categories } = useMenu();
  const { cart } = useCart();
  const [selectedCategory, setSelectedCategory] = useState(null);

  // Filter categories with menu items only
  let filteredCategories = categories.filter(cat =>
    menuItems.some(item => String(item.category_id) === String(cat.id))
  );

  // If no categories have menu items, fall back to all categories
  if (filteredCategories.length === 0 && categories.length > 0) {
    filteredCategories = categories;
  }

  // Set selectedCategory to the first available category when categories change
  useEffect(() => {
    if (filteredCategories.length > 0 && !selectedCategory) {
      setSelectedCategory(String(filteredCategories[0].id));
    }
  }, [filteredCategories, selectedCategory]);

  // Get items for selected category
  const items = menuItems.filter(item => String(item.category_id) === String(selectedCategory));

  // Debugging
  console.log('categories:', categories);
  console.log('menuItems:', menuItems);
  console.log('filteredCategories:', filteredCategories);
  console.log('selectedCategory:', selectedCategory);
  console.log('items:', items);

  return (
    <Container maxWidth="sm" sx={{ pb: 10 }}>
      {filteredCategories.length === 0 || !selectedCategory ? (
        <Typography align="center" color="text.secondary" sx={{ mt: 4 }}>
          No categories found.
        </Typography>
      ) : (
        <>
          {/* Category Tabs */}
          <Tabs
            value={selectedCategory}
            onChange={(e, val) => setSelectedCategory(val)}
            variant="scrollable"
            scrollButtons="auto"
            sx={{ mb: 2 }}
          >
            {filteredCategories.map(cat => (
              <Tab key={cat.id} label={cat.name} value={String(cat.id)} />
            ))}
          </Tabs>

          {/* Category Heading */}
          <Typography variant="h5" sx={{ fontWeight: 700, mb: 2 }}>
            {filteredCategories.find(cat => String(cat.id) === String(selectedCategory))?.name}
          </Typography>

          {/* Menu Items */}
          <Box>
            {items.length === 0 ? (
              <Typography align="center" color="text.secondary" sx={{ mt: 4 }}>
                No items found for this category.
              </Typography>
            ) : (
              items.map(item => (
                <Paper key={item.id} sx={{ display: 'flex', alignItems: 'center', mb: 2, p: 2 }}>
                  <Box sx={{ flex: 1 }}>
                    <Typography variant="h6" sx={{ fontWeight: 700 }}>{item.name}</Typography>
                    <Typography variant="body1" sx={{ fontWeight: 600 }}>
                      £{item.price?.toFixed(2)}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" noWrap>
                      {item.description}
                    </Typography>
                  </Box>
                </Paper>
              ))
            )}
          </Box>
        </>
      )}
      {/* Go to Basket Button */}
      <Button
        variant="contained"
        color="primary"
        fullWidth
        sx={{
          position: 'fixed',
          left: 0,
          bottom: 0,
          width: '100vw',
          borderRadius: 0,
          py: 2,
          zIndex: 1200,
        }}
        onClick={() => {/* navigate to cart */}}
      >
        Go to Basket
      </Button>
    </Container>
  );
};

export default Menu;
