import React, { useEffect, useState } from 'react';
import {
  Box, Typography, Alert, Button, TextField, Paper, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent,
  DialogContentText, DialogActions, Stack, Select, MenuItem, InputLabel, FormControl
} from '@mui/material';
import { supabase } from '../supabaseClient';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const emptyCategory = { name: '', type: 'category', image_url: '', cover_image_url: '' };
const emptySubcategory = { name: '', parent_id: null, type: 'subcategory' };

function CategoryManager() {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [form, setForm] = useState(emptyCategory);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [deleteId, setDeleteId] = useState(null);
  const [isSubcategory, setIsSubcategory] = useState(false);
  const [imageFile, setImageFile] = useState(null);
  const [coverImageFile, setCoverImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [coverImagePreview, setCoverImagePreview] = useState('');

  const fetchCategories = async () => {
    const { data, error } = await supabase.from('categories').select('*').eq('type', 'category');
    if (error) setError(error.message);
    else setCategories(data);
  };

  const fetchSubcategories = async () => {
    const { data, error } = await supabase.from('categories').select('*').eq('type', 'subcategory');
    if (error) setError(error.message);
    else setSubcategories(data);
  };

  useEffect(() => {
    fetchCategories();
    fetchSubcategories();
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImageFile(file);
    setImagePreview(file ? URL.createObjectURL(file) : '');
  };

  const handleCoverImageChange = (e) => {
    const file = e.target.files[0];
    setCoverImageFile(file);
    setCoverImagePreview(file ? URL.createObjectURL(file) : '');
  };

  const uploadImage = async (file, folder) => {
    if (!file) return '';
    const fileExt = file.name.split('.').pop();
    const fileName = `${folder}/${Date.now()}-${Math.random().toString(36).substr(2, 9)}.${fileExt}`;
    const { data, error } = await supabase.storage.from('category-images').upload(fileName, file);
    if (error) throw new Error(error.message);
    const { publicURL } = supabase.storage.from('category-images').getPublicUrl(fileName).data;
    return publicURL;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!form.name) {
      setError('Name is required.');
      return;
    }
    if (isSubcategory && !form.parent_id) {
      setError('Please select a parent category.');
      return;
    }
    let imageUrl = form.image_url;
    let coverImageUrl = form.cover_image_url;
    try {
      if (!isSubcategory) {
        if (imageFile) imageUrl = await uploadImage(imageFile, 'category');
        if (coverImageFile) coverImageUrl = await uploadImage(coverImageFile, 'cover');
      }
    } catch (err) {
      setError('Image upload failed: ' + err.message);
      return;
    }
    const submitData = {
      ...form,
      image_url: !isSubcategory ? imageUrl : undefined,
      cover_image_url: !isSubcategory ? coverImageUrl : undefined,
    };
    if (editingId) {
      const { error } = await supabase.from('categories').update(submitData).eq('id', editingId);
      if (error) setError(error.message);
      else {
        setSuccess('Item updated!');
        setEditingId(null);
        setForm(isSubcategory ? emptySubcategory : emptyCategory);
        setImageFile(null); setCoverImageFile(null);
        setImagePreview(''); setCoverImagePreview('');
        fetchCategories();
        fetchSubcategories();
      }
    } else {
      const { error } = await supabase.from('categories').insert([submitData]);
      if (error) setError(error.message);
      else {
        setSuccess('Item added!');
        setForm(isSubcategory ? emptySubcategory : emptyCategory);
        setImageFile(null); setCoverImageFile(null);
        setImagePreview(''); setCoverImagePreview('');
        fetchCategories();
        fetchSubcategories();
      }
    }
  };

  const handleEdit = item => {
    setEditingId(item.id);
    setForm({
      name: item.name,
      type: item.type,
      parent_id: item.parent_id,
      image_url: item.image_url || '',
      cover_image_url: item.cover_image_url || '',
    });
    setIsSubcategory(item.type === 'subcategory');
    setImagePreview(item.image_url || '');
    setCoverImagePreview(item.cover_image_url || '');
    setImageFile(null);
    setCoverImageFile(null);
  };

  const handleDelete = async () => {
    if (!deleteId) return;
    const { error } = await supabase.from('categories').delete().eq('id', deleteId);
    if (error) setError(error.message);
    else {
      setSuccess('Item deleted!');
      fetchCategories();
      fetchSubcategories();
    }
    setDeleteId(null);
  };

  const handleCancel = () => {
    setEditingId(null);
    setForm(isSubcategory ? emptySubcategory : emptyCategory);
    setImageFile(null); setCoverImageFile(null);
    setImagePreview(''); setCoverImagePreview('');
    setError('');
    setSuccess('');
  };

  const toggleType = () => {
    setIsSubcategory(!isSubcategory);
    setForm(isSubcategory ? emptyCategory : emptySubcategory);
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>Category Management</Typography>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
      <Paper sx={{ p: 2, mb: 4 }}>
        <form onSubmit={handleSubmit}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} alignItems="center">
            <TextField
              name="name"
              label={isSubcategory ? 'Subcategory Name' : 'Category Name'}
              value={form.name}
              onChange={handleChange}
              required
              size="small"
            />
            {!isSubcategory && (
              <>
                <Button variant="outlined" component="label" size="small">
                  Upload Category Image
                  <input type="file" accept="image/*" hidden onChange={handleImageChange} />
                </Button>
                {imagePreview && (
                  <img src={imagePreview} alt="Category" style={{ width: 48, height: 48, objectFit: 'cover', borderRadius: 8, marginLeft: 8 }} />
                )}
                <Button variant="outlined" component="label" size="small">
                  Upload Cover Image
                  <input type="file" accept="image/*" hidden onChange={handleCoverImageChange} />
                </Button>
                {coverImagePreview && (
                  <img src={coverImagePreview} alt="Cover" style={{ width: 80, height: 48, objectFit: 'cover', borderRadius: 8, marginLeft: 8 }} />
                )}
              </>
            )}
            {isSubcategory && (
              <FormControl size="small" sx={{ minWidth: 180 }} required>
                <InputLabel id="parent-category-label">Parent Category</InputLabel>
                <Select
                  labelId="parent-category-label"
                  name="parent_id"
                  value={form.parent_id || ''}
                  label="Parent Category"
                  onChange={e => setForm({ ...form, parent_id: e.target.value })}
                >
                  {categories.map(cat => (
                    <MenuItem key={cat.id} value={cat.id}>{cat.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            )}
            <Button type="button" variant="outlined" onClick={toggleType}>
              {isSubcategory ? 'Add Category' : 'Add Subcategory'}
            </Button>
            <Button type="submit" variant="contained" color="primary">
              {editingId ? 'Update' : 'Add'}
            </Button>
            {editingId && (
              <Button type="button" variant="outlined" color="secondary" onClick={handleCancel}>
                Cancel
              </Button>
            )}
          </Stack>
        </form>
      </Paper>
      <Typography variant="h6" gutterBottom>Categories</Typography>
      <TableContainer component={Paper} sx={{ mb: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Actions</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {categories.map(item => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEdit(item)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => setDeleteId(item.id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Typography variant="h6" gutterBottom>Subcategories</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Parent ID</b></TableCell>
              <TableCell><b>Actions</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {subcategories.map(item => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.parent_id}</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEdit(item)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => setDeleteId(item.id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Dialog open={!!deleteId} onClose={() => setDeleteId(null)}>
        <DialogTitle>Delete Item</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteId(null)}>Cancel</Button>
          <Button onClick={handleDelete} color="error">Delete</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default CategoryManager;
