import React, { useEffect, useState } from 'react';
import {
  Box, Typography, Alert, Button, TextField, Paper, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent,
  DialogContentText, DialogActions, Stack, CircularProgress
} from '@mui/material';
import { supabase } from '../supabaseClient';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const emptyItem = { name: '', description: '', price: '', image_url: '', category: '', subcategory: '' };

function MenuManager() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState(emptyItem);
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [deleteId, setDeleteId] = useState(null);
  const [uploading, setUploading] = useState(false);

  const fetchItems = async () => {
    const { data, error } = await supabase.from('menu_items').select('*');
    if (error) setError(error.message);
    else setItems(data);
  };

  useEffect(() => { fetchItems(); }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setUploading(true);
    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${Math.random()}.${fileExt}`;
      const { data, error } = await supabase.storage
        .from('menu-images')
        .upload(fileName, file);
      if (error) throw error;
      const { data: { publicUrl } } = supabase.storage
        .from('menu-images')
        .getPublicUrl(data.path);
      setForm({ ...form, image_url: publicUrl });
    } catch (err) {
      setError(err.message);
    } finally {
      setUploading(false);
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!form.name || !form.price) {
      setError('Name and price are required.');
      return;
    }
    if (editingId) {
      const { error } = await supabase.from('menu_items').update(form).eq('id', editingId);
      if (error) setError(error.message);
      else {
        setSuccess('Item updated!');
        setEditingId(null);
        setForm(emptyItem);
        fetchItems();
      }
    } else {
      const { error } = await supabase.from('menu_items').insert([{ ...form, price: parseFloat(form.price) }]);
      if (error) setError(error.message);
      else {
        setSuccess('Item added!');
        setForm(emptyItem);
        fetchItems();
      }
    }
  };

  const handleEdit = item => {
    setEditingId(item.id);
    setForm({
      name: item.name,
      description: item.description,
      price: item.price,
      image_url: item.image_url,
      category: item.category,
      subcategory: item.subcategory,
    });
  };

  const handleDelete = async () => {
    if (!deleteId) return;
    const { error } = await supabase.from('menu_items').delete().eq('id', deleteId);
    if (error) setError(error.message);
    else {
      setSuccess('Item deleted!');
      fetchItems();
    }
    setDeleteId(null);
  };

  const handleCancel = () => {
    setEditingId(null);
    setForm(emptyItem);
    setError('');
    setSuccess('');
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom>Menu Management</Typography>
      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
      <Paper sx={{ p: 2, mb: 4 }}>
        <form onSubmit={handleSubmit}>
          <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2} alignItems="center">
            <TextField
              name="name"
              label="Name"
              value={form.name}
              onChange={handleChange}
              required
              size="small"
            />
            <TextField
              name="description"
              label="Description"
              value={form.description}
              onChange={handleChange}
              size="small"
            />
            <TextField
              name="price"
              label="Price"
              type="number"
              inputProps={{ step: '0.01' }}
              value={form.price}
              onChange={handleChange}
              required
              size="small"
              sx={{ width: 100 }}
            />
            <input
              accept="image/*"
              type="file"
              id="image-upload"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
            <label htmlFor="image-upload">
              <Button variant="outlined" component="span" disabled={uploading}>
                {uploading ? <CircularProgress size={24} /> : 'Upload Image'}
              </Button>
            </label>
            {form.image_url && (
              <Box sx={{ width: 100, height: 100, overflow: 'hidden' }}>
                <img src={form.image_url} alt="Preview" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
              </Box>
            )}
            <TextField
              name="category"
              label="Category"
              value={form.category}
              onChange={handleChange}
              size="small"
            />
            <TextField
              name="subcategory"
              label="Subcategory"
              value={form.subcategory}
              onChange={handleChange}
              size="small"
            />
            <Button type="submit" variant="contained" color="primary">
              {editingId ? 'Update' : 'Add'}
            </Button>
            {editingId && (
              <Button type="button" variant="outlined" color="secondary" onClick={handleCancel}>
                Cancel
              </Button>
            )}
          </Stack>
        </form>
      </Paper>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Description</b></TableCell>
              <TableCell><b>Price</b></TableCell>
              <TableCell><b>Category</b></TableCell>
              <TableCell><b>Subcategory</b></TableCell>
              <TableCell><b>Actions</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map(item => (
              <TableRow key={item.id}>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.description}</TableCell>
                <TableCell>${item.price}</TableCell>
                <TableCell>{item.category}</TableCell>
                <TableCell>{item.subcategory}</TableCell>
                <TableCell>
                  <IconButton color="primary" onClick={() => handleEdit(item)}><EditIcon /></IconButton>
                  <IconButton color="error" onClick={() => setDeleteId(item.id)}><DeleteIcon /></IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Dialog open={!!deleteId} onClose={() => setDeleteId(null)}>
        <DialogTitle>Delete Menu Item</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete this menu item? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteId(null)}>Cancel</Button>
          <Button onClick={handleDelete} color="error">Delete</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default MenuManager;
