import React from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  CircularProgress,
  Alert,
} from '@mui/material';
import { useMenu } from '../context/MenuContext';
import { useCart } from '../context/CartContext';

const Menu = () => {
  const { menuItems, categories, loading, error } = useMenu();
  const { cart, addToCart, removeFromCart } = useCart();

  // Helper to check if item is in cart
  const isInCart = (itemId) => cart.some((cartItem) => cartItem.id === itemId);

  // Toggle cart action
  const handleCartToggle = (item) => {
    if (isInCart(item.id)) {
      removeFromCart(item.id);
    } else {
      addToCart(item);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container>
        <Alert severity="error" sx={{ mt: 2 }}>
          {error}
        </Alert>
      </Container>
    );
  }

  // Group menu items by category name (support both category_id and category name)
  // Build a map of category id to name
  const categoryIdToName = categories.reduce((acc, cat) => {
    if (cat.type === 'category') acc[cat.id] = cat.name;
    return acc;
  }, {});

  // Group items by category name
  const itemsByCategoryName = {};
  menuItems.forEach(item => {
    // Prefer category_id if present, else fallback to category field
    let catName = '';
    if (item.category_id && categoryIdToName[item.category_id]) {
      catName = categoryIdToName[item.category_id];
    } else if (item.category) {
      catName = item.category;
    }
    if (!catName) catName = 'Uncategorized';
    if (!itemsByCategoryName[catName]) itemsByCategoryName[catName] = [];
    itemsByCategoryName[catName].push(item);
  });

  const categoryNames = Object.keys(itemsByCategoryName);

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        Our Menu
      </Typography>
      {categoryNames.length === 0 ? (
        <Typography align="center" color="text.secondary">No menu items found.</Typography>
      ) : (
        categoryNames.map(catName => (
          <Box key={catName} sx={{ mb: 6 }}>
            <Typography variant="h5" component="h2" sx={{ mb: 3, fontWeight: 600 }}>
              {catName}
            </Typography>
            <Grid container spacing={4}>
              {itemsByCategoryName[catName].map((item) => (
                <Grid item key={item.id} xs={12} sm={6} md={4}>
                  <Card 
                    sx={{ 
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column',
                      transition: 'transform 0.2s',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: 3,
                      },
                    }}
                  >
                    {item.image_url && (
                      <CardMedia
                        component="img"
                        height="200"
                        image={item.image_url}
                        alt={item.name}
                        sx={{ objectFit: 'cover' }}
                      />
                    )}
                    <CardContent sx={{ flexGrow: 1 }}>
                      <Typography gutterBottom variant="h6" component="h2">
                        {item.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                        {item.description}
                      </Typography>
                      <Typography variant="h6" color="primary" sx={{ mb: 2 }}>
                        {item.price?.toLocaleString('en-GB', { style: 'currency', currency: 'GBP' })}
                      </Typography>
                      <Button
                        variant={isInCart(item.id) ? 'outlined' : 'contained'}
                        color={isInCart(item.id) ? 'success' : 'primary'}
                        onClick={() => handleCartToggle(item)}
                        fullWidth
                      >
                        {isInCart(item.id) ? 'Added to Cart' : 'Add to Cart'}
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        ))
      )}
    </Container>
  );
};

export default Menu;
