import React, { useEffect, useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  Stack,
  useTheme,
  useMediaQuery,
  Tabs,
  Tab,
  Paper,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import LocalDiningIcon from '@mui/icons-material/LocalDining';
import RestaurantIcon from '@mui/icons-material/Restaurant';
import FastfoodIcon from '@mui/icons-material/Fastfood';
import LocalPizzaIcon from '@mui/icons-material/LocalPizza';
import { supabase } from '../supabaseClient'; // adjust path if needed
import { useMenu } from '../context/MenuContext';
import { useCart } from '../context/CartContext';
import AppRoutes from '../AppRoutes';

const HeroSection = styled(Box)(({ theme }) => ({
  background: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80')`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  height: '80vh',
  display: 'flex',
  alignItems: 'center',
  color: 'white',
  textAlign: 'center',
  position: 'relative',
}));

const CategoryCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  cursor: 'pointer',
  '&:hover': {
    transform: 'translateY(-8px)',
    transition: 'transform 0.3s ease-in-out',
  },
}));

const PopularItemCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  cursor: 'pointer',
  '&:hover': {
    transform: 'translateY(-8px)',
    transition: 'transform 0.3s ease-in-out',
  },
}));

const Home = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const navigate = useNavigate();
  const { menuItems, categories } = useMenu();
  const { cart } = useCart();
  const [selectedCategory, setSelectedCategory] = useState('');

  // Set selectedCategory to the first available category when categories change
  useEffect(() => {
    if (categories.length > 0) {
      setSelectedCategory(categories[0].id);
    }
  }, [categories]);

  // Filter categories with menu items only
  const filteredCategories = categories.filter(cat =>
    menuItems.some(item => item.category_id === cat.id)
  );

  // Get items for selected category
  const items = menuItems.filter(item => String(item.category_id) === String(selectedCategory));

  const featuredItems = [
    {
      id: 1,
      name: 'Classic Burger',
      description: 'Juicy beef patty with fresh vegetables and special sauce',
      image: 'https://images.unsplash.com/photo-1568901346375-23c9450c58cd?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      price: '£12.99',
    },
    {
      id: 2,
      name: 'Margherita Pizza',
      description: 'Fresh tomatoes, mozzarella, and basil on our signature crust',
      image: 'https://images.unsplash.com/photo-1574071318508-1cdbab80d002?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      price: '£8.99',
    },
    {
      id: 3,
      name: 'Grilled Salmon',
      description: 'Fresh Atlantic salmon with seasonal vegetables',
      image: 'https://images.unsplash.com/photo-1519708227418-c8fd9a32b7a2?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      price: '£24.99',
    },
    {
      id: 4,
      name: 'Caesar Salad',
      description: 'Crisp romaine lettuce with parmesan and croutons',
      image: 'https://images.unsplash.com/photo-1550304943-4f24f54ddde9?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60',
      price: '£14.99',
    },
  ];

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      setLoading(true);
      setError(null);
      const { data, error } = await supabase
        .from('categories')
        .select('id, name, image_url')
        .eq('type', 'category');
      if (error) {
        setError('Failed to fetch categories');
      } else {
        // Filter categories with menu items only
        const filteredCategories = data.filter(cat =>
          menuItems.some(item => item.category_id === cat.id)
        );
        setCategories(filteredCategories);
      }
      setLoading(false);
    };
    fetchCategories();

    // Real-time updates
    const subscription = supabase
      .channel('categories_changes')
      .on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'categories' },
        () => {
          fetchCategories();
        }
      )
      .subscribe();

    return () => {
      subscription.unsubscribe();
    };
  }, [menuItems]);

  console.log('categories:', categories);
  console.log('menuItems:', menuItems);
  console.log('filteredCategories:', filteredCategories);
  console.log('selectedCategory:', selectedCategory);
  console.log('items:', items);

  return (
    <Box>
      {/* Hero Section */}
      <HeroSection>
        <Container maxWidth="md">
          <Typography
            variant="h1"
            component="h1"
            sx={{
              fontSize: { xs: '2.5rem', md: '4rem' },
              fontWeight: 700,
              mb: 2,
            }}
          >
            Delicious Food Delivered To Your Doorstep
          </Typography>
          <Typography
            variant="h5"
            sx={{
              mb: 4,
              fontSize: { xs: '1.2rem', md: '1.5rem' },
            }}
          >
            Order your favorite meals from the best restaurants in town
          </Typography>
          <Stack
            direction={{ xs: 'column', sm: 'row' }}
            spacing={2}
            justifyContent="center"
          >
            <Button
              variant="contained"
              size="large"
              onClick={() => navigate('/menu')}
              sx={{
                py: 1.5,
                px: 4,
                fontSize: '1.1rem',
              }}
            >
              Order Now
            </Button>
            <Button
              variant="outlined"
              size="large"
              onClick={() => navigate('/about')}
              sx={{
                py: 1.5,
                px: 4,
                fontSize: '1.1rem',
                color: 'white',
                borderColor: 'white',
                '&:hover': {
                  borderColor: 'white',
                  backgroundColor: 'rgba(255, 255, 255, 0.1)',
                },
              }}
            >
              Learn More
            </Button>
          </Stack>
        </Container>
      </HeroSection>

      {/* Categories Section */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Typography
          variant="h2"
          component="h2"
          align="center"
          sx={{ mb: 6, fontWeight: 600 }}
        >
          Food Categories
        </Typography>
        {loading ? (
          <Typography align="center">Loading categories...</Typography>
        ) : error ? (
          <Typography align="center" color="error">{error}</Typography>
        ) : (
          <Tabs
            value={selectedCategory}
            onChange={(e, val) => setSelectedCategory(val)}
            variant="scrollable"
            scrollButtons="auto"
            sx={{ mb: 2 }}
          >
            {filteredCategories.map(cat => (
              <Tab key={cat.id} label={cat.name} value={cat.id} />
            ))}
          </Tabs>
        )}
      </Container>

      {/* Category Heading */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Typography
          variant="h5"
          sx={{ fontWeight: 700, mb: 2 }}
        >
          {filteredCategories.find(cat => cat.id === selectedCategory)?.name}
        </Typography>
      </Container>

      {/* Menu Items */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Box>
          {menuItems.map(item => (
            <div key={item.id}>{item.name}</div>
          ))}
        </Box>
      </Container>

      {/* Features Section */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Grid container spacing={4} alignItems="center">
          <Grid item xs={12} md={6}>
            <Typography variant="h2" component="h2" sx={{ mb: 3, fontWeight: 600 }}>
              Why Choose Us?
            </Typography>
            <Stack spacing={3}>
              <Box>
                <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                  Fast Delivery
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Get your food delivered within 30 minutes or it's free!
                </Typography>
              </Box>
              <Box>
                <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                  Fresh Food
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  We only work with the best restaurants to ensure quality food.
                </Typography>
              </Box>
              <Box>
                <Typography variant="h5" sx={{ mb: 1, fontWeight: 600 }}>
                  24/7 Support
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Our customer service team is always ready to help you.
                </Typography>
              </Box>
            </Stack>
          </Grid>
          <Grid item xs={12} md={6}>
            <Box
              component="img"
              src="https://images.unsplash.com/photo-1555396273-367ea4eb4db5?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80"
              alt="Food delivery"
              sx={{
                width: '100%',
                height: 'auto',
                borderRadius: 2,
                boxShadow: 3,
              }}
            />
          </Grid>
        </Grid>
      </Container>

      {/* Go to Basket Button */}
      <Button
        variant="contained"
        color="primary"
        fullWidth
        sx={{
          position: 'fixed',
          left: 0,
          bottom: 0,
          width: '100vw',
          borderRadius: 0,
          py: 2,
          zIndex: 1200,
        }}
        onClick={() => {/* navigate to cart */}}
      >
        Go to Basket
      </Button>
    </Box>
  );
};

export default Home;
