import React, { useState } from 'react';
import {
  Container,
  Tabs,
  Tab,
  Box,
  Typography,
  Button,
  Paper,
} from '@mui/material';
import { useMenu } from '../context/MenuContext';
import { useCart } from '../context/CartContext';

const Menu = () => {
  const { menuItems, categories } = useMenu();
  const { cart } = useCart();
  const [selectedCategory, setSelectedCategory] = useState(categories[0]?.id || '');

  // Filter categories with menu items only
  const filteredCategories = categories.filter(cat =>
    menuItems.some(item => item.category_id === cat.id)
  );

  // Get items for selected category
  const items = menuItems.filter(item => item.category_id === selectedCategory);

  return (
    <Container maxWidth="sm" sx={{ pb: 10 }}>
      {/* Category Tabs */}
      <Tabs
        value={selectedCategory}
        onChange={(e, val) => setSelectedCategory(val)}
        variant="scrollable"
        scrollButtons="auto"
        sx={{ mb: 2 }}
      >
        {filteredCategories.map(cat => (
          <Tab key={cat.id} label={cat.name} value={cat.id} />
        ))}
      </Tabs>

      {/* Category Heading */}
      <Typography variant="h5" sx={{ fontWeight: 700, mb: 2 }}>
        {filteredCategories.find(cat => cat.id === selectedCategory)?.name}
      </Typography>

      {/* Menu Items */}
      <Box>
        {items.map(item => (
          <Paper key={item.id} sx={{ display: 'flex', alignItems: 'center', mb: 2, p: 2 }}>
            <Box sx={{ flex: 1 }}>
              <Typography variant="h6" sx={{ fontWeight: 700 }}>{item.name}</Typography>
              <Typography variant="body1" sx={{ fontWeight: 600 }}>
                £{item.price?.toFixed(2)} · {item.points || 0} points
              </Typography>
              <Typography variant="body2" color="text.secondary" noWrap>
                {item.description}
              </Typography>
            </Box>
            {item.image_url && (
              <Box
                component="img"
                src={item.image_url}
                alt={item.name}
                sx={{ width: 90, height: 90, objectFit: 'cover', borderRadius: 2, ml: 2 }}
              />
            )}
          </Paper>
        ))}
      </Box>

      {/* Go to Basket Button */}
      <Button
        variant="contained"
        color="primary"
        fullWidth
        sx={{
          position: 'fixed',
          left: 0,
          bottom: 0,
          width: '100vw',
          borderRadius: 0,
          py: 2,
          zIndex: 1200,
        }}
        onClick={() => {/* navigate to cart */}}
      >
        Go to Basket
      </Button>
    </Container>
  );
};

export default Menu;
